import useFetch from 'react-fetch-hook'
import ContactCards from './ContactCards'
import {useEffect, useState} from 'react'

const App = () => {
  const url = 'https://jsonplaceholder.typicode.com'
  const { isLoading, data, error } = useFetch(url+'/users')
  const [contactList, setContactList] = useState()
  const [filterQuery, setFilterQuery] = useState()

  useEffect(() => {
    if (!filterQuery) {
      setContactList(data?.slice(0, 10))
    } else {
      const queryString = filterQuery.toLowerCase()
      const filteredData = data.filter(contact => {
        // if it's just one letter, return all names that start with it
        if (queryString.length === 1) {
          const firstLetter = contact.name.charAt(0).toLowerCase()
          return firstLetter === queryString
        }
        else {
          return contact.name.toLowerCase().includes(queryString)
        }
      })
      setContactList(filteredData)
    }
  }, [data, filterQuery])

  return (
    <div className={"bg-gray-100"}>
      <section>
        <form>
          <input
            type={"text"}
            placeholder={"type here to filter..."}
            onChange={event => setFilterQuery(event.target.value)}
            className={"ml-20 mt-6 rounded-md p-2"}
          />
        </form>
      </section>
      <section className={"grid sm:grid-cols-2 md:grid-cols-4 gap-6 p-20"}>
        {contactList?.length < 1 && (
          <h1>No data matches your search</h1>
        )}
        <ContactCards contactList={contactList}/>
      </section>
    </div>
  )
}

(document).ready(function() {
  (".menu-icon").on("click", function() {
        ("nav ul").toggleClass("showing");
  });
});

// Scrolling Effect

(window).on("scroll", function() {
  if((window).scrollTop()) {
        ('nav').addClass('black');
  }

  else {
        $('nav').removeClass('black');
  }
})

export default App
